Atmo_QoA_Payload = {
    'decodeUp': function (port,payload) {

      var FLAG_TEMP = 0x01;
      var FLAG_HUM = 0x02;
      var FLAG_PRES = 0x04;
      var FLAG_PM10 = 0x08;
      var FLAG_PM25 = 0x10;
      var FLAG_VOC = 0x20;
      var FLAG_ECO2 = 0x40;
      var FLAG_CO2 = 0x80;
      var FLAG_NO2 = 0x0100;
      var FLAG_O3 = 0x0200;
      var FLAG_GNSS = 0x0400;
      var FLAG_GNSSPREV = 0x0800;
      var FLAG_HOUR = 0x4000;
      var FLAG_ERROR = 0x8000;

      var p = payload;
      var l = p.length;

	  if (l >= 2) {
        var index = 2;
        var h = p.readUInt16BE(0);

	    	var value = {};

        if((h & FLAG_TEMP) !== 0) {
          if((index + 1) <= l) {
            var data = p.readUInt8(index);
  	        value["temperature"]=(data/2.0) - 30;
            index += 1;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_HUM) !== 0) {
          if((index + 1) <= l) {
            var data = p.readUInt8(index);
  	        value["humidity"]=(data/2.0);
            index += 1;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_PRES) !== 0) {
          if((index + 1) <= l) {
            var data = p.readUInt8(index);
  	        value["pressure"]=(data+900);
            index += 1;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_PM10) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["pm10"]=(data);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_PM25) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["pm25"]=(data);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_VOC) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["voc"]=(data);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_ECO2) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["eco2"]=(data);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_CO2) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["co2"]=(data);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_NO2) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["no2"]=(data*0.5);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_O3) !== 0) {
          if((index + 2) <= l) {
            var data = p.readUInt16BE(index);
  	        value["o3"]=(data*0.5);
            index += 2;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_GNSS) !== 0) {
          if((index + 8) <= l) {
            var lat = p.readFloat(index);
  	        value["latitude"]=(lat);
            index += 4;
            var lng = p.readFloat(index);
  	        value["longitude"]=(lng);
            index += 4;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

        if((h & FLAG_GNSSPREV) !== 0) {
          value["gnss_prev"]=true;
        }

        if((h & FLAG_HOUR) !== 0) {
          value["hour"]=true;
        }

        if((h & FLAG_ERROR) !== 0) {
          if((index + 1) <= l) {
            var data = p.readUInt8(index);
  	        value["error"]=data;
            index += 1;
          } else {
            value["overflow"] = true;
            return value;
          }
        }

	    	return value;
	    } else {
        return undefined;
      }
	}
}
