function getSubBandsEU868(frequency) {
    if(frequency>=863000000 && frequency<868000000) {
        return "g";
    } else if(frequency>=868000000 && frequency<868600000) {
        return "g1";
    } else if(frequency>=868700000 && frequency<869200000) {
        return "g2";
    } else if(frequency>=869400000 && frequency<869650000) {
        return "g3";
    } else if(frequency>=869700000 && frequency<870000000) {
        return "g4";
    } else {
        return undefined;
    }
    /* Duty Cycle */
    /*
    g (863.0  868.0 MHz): 1%
    g1 (868.0  868.6 MHz): 1%
    g2 (868.7  869.2 MHz): 0.1%
    g3 (869.4  869.65 MHz): 10%
    g4 (869.7  870.0 MHz): 1%
    */
}

